// App.js

import React, {useState, useEffect, useCallback} from 'react';
import {useNavigate, Link} from 'react-router-dom';
import LoginPage from './LoginPage';
import {jwtDecode} from 'jwt-decode';

const App = () => {
    const [token, setToken] = useState('');
    const [posts, setPosts] = useState([]);
    const [users, setUsers] = useState([]);
    const [decodedToken, setDecodedToken] = useState({});
    const navigate = useNavigate();

    const fetchData = useCallback(async (token) => {
        try {
            const postsResponse = await fetch('/posts', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (postsResponse.ok) {
                const postsData = await postsResponse.json();
                const postsWithComments = await Promise.all(
                    postsData.map(async (post) => {
                        const commentsResponse = await fetch(`/posts/${post.post_id}/comments`, {
                            headers: {
                                Authorization: `Bearer ${token}`,
                            },
                        });

                        if (commentsResponse.ok) {
                            const commentsData = await commentsResponse.json();
                            return {...post, comments: commentsData.comments};
                        } else {
                            console.error('Error fetching comments:', commentsResponse.statusText);
                            return post;
                        }
                    })
                );

                setPosts(postsWithComments);
            } else {
                sessionStorage.removeItem('token');
                navigate('/login');
            }

            const usersResponse = await fetch('/users', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (usersResponse.ok) {
                const usersData = await usersResponse.json();
                setUsers(usersData);
            } else {
                sessionStorage.removeItem('token');
                navigate('/login');
            }
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }, [navigate]);

    const handleLogin = async (enteredToken) => {
        sessionStorage.setItem('token', enteredToken);
        setToken(enteredToken);

        try {
            const decoded = jwtDecode(enteredToken);
            setDecodedToken(decoded);
            await fetchData(enteredToken);
            navigate('/posts');
        } catch (error) {
            console.error('Error decoding token:', error);
            navigate('/login');
        }
    };

    const handleLogout = () => {
        sessionStorage.removeItem('token');
        setToken('');
    };

    useEffect(() => {
        const storedToken = sessionStorage.getItem('token');
        if (storedToken) {
            setToken(storedToken);
            fetchData(storedToken);
        }
    }, [fetchData]);

    return (
        <div className="container">
            <header className="header">
                <div className="header-buttons">
                    <Link to="/posts" className="button">
                        <button>Posts</button>
                    </Link>
                    <Link to={token ? `/users/${decodedToken.user_id}` : '/login'} className="button">
                        <button>My Profile</button>
                    </Link>
                </div>
                <div>
                    <button className="logout-button" onClick={handleLogout}>
                        Logout
                    </button>
                </div>
            </header>
            {token ? (
                <div className="page-content">
                    <div className="posts-container">
                        <h1>Posts</h1>
                        {posts.length === 0 ? (
                            <p>Loading posts...</p>
                        ) : (
                            posts.map((post) => (
                                <div key={post.post_id} className="post">
                                    <div className="post-header">
                                        Author: <Link to={`/users/${user.user_id}`} className="user-link">
                                        {user.username}
                                    </Link>
                                    </div>
                                    <div className="post-content">
                                        {post.post_content}
                                    </div>
                                    <div className="comments-section">
                                        <h2 className="comments-heading">Comments</h2>
                                        {post.comments.length === 0 ? (
                                            <p>No comments yet.</p>
                                        ) : (
                                            <ul className="comments-list">
                                                {post.comments.map((comment) => (
                                                    <li key={comment.comment_id} className="comment-item">
                                                        {comment.comment_content}
                                                    </li>
                                                ))}
                                            </ul>
                                        )}
                                    </div>
                                </div>
                            ))
                        )}
                    </div>
                    <div className="users-container">
                        <h1>Users</h1>
                        {users.length === 0 ? (
                            <p>Loading users...</p>
                        ) : (
                            <ul className="user-list">
                                {users.map((user) => (
                                    <li key={user.user_id} className="user-item">
                                        <Link to={`/users/${user.user_id}`} className="user-link">
                                            {user.username}
                                        </Link>
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div>
                </div>
            ) : (
                <LoginPage onLogin={handleLogin}/>
            )}
        </div>
    );
};

export default App;
