// App.js

import React, {useState, useEffect, useCallback} from 'react';
import {useNavigate} from 'react-router-dom';
import LoginPage from './LoginPage';


const App = () => {
    const [token, setToken] = useState('');
    const [posts, setPosts] = useState([]);
    const [users, setUsers] = useState([]);
    const navigate = useNavigate();


    const fetchData = useCallback(async (token) => {
        try {
            const postsResponse = await fetch('/posts', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            const usersResponse = await fetch('/users', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (postsResponse.ok) {
                const postsData = await postsResponse.json();
                setPosts(postsData);
            } else {
                sessionStorage.removeItem('token');
                navigate('/login');
                return;
            }

            if (usersResponse.ok) {
                const usersData = await usersResponse.json();
                setUsers(usersData);
            } else {
                sessionStorage.removeItem('token');
                navigate('/login');
            }
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }, [navigate]);

    const handleLogin = async (enteredToken) => {
        sessionStorage.setItem('token', enteredToken);
        setToken(enteredToken);
        await fetchData(enteredToken);
        navigate('/posts');
    };

    const handleLogout = () => {
        sessionStorage.removeItem('token');
        setToken('');
    };

    useEffect(() => {
        const storedToken = sessionStorage.getItem('token');

        if (storedToken) {
            setToken(storedToken);
            fetchData(storedToken);
        }
    }, [fetchData]);


    return (
        <div style={{display: 'flex', flexDirection: 'column', height: '100vh', backgroundColor: '#f0f0f0'}}>
            {token ? (
                <div style={{flex: 1, display: 'flex', justifyContent: 'space-between', padding: '10px'}}>
                    <div style={{
                        width: '70%',
                        overflowY: 'auto',
                        padding: '10px',
                        backgroundColor: '#fff',
                        borderRadius: '8px'
                    }}>
                        <h1>Posts</h1>
                        {posts.length === 0 ? (
                            <p>Loading posts...</p>
                        ) : (
                            posts.map((post) => (
                                <div key={post.post_id} style={{
                                    border: '1px solid #ddd',
                                    borderRadius: '8px',
                                    padding: '10px',
                                    marginBottom: '10px'
                                }}>
                                    <h3>{post.post_content}</h3>
                                    <a href={`/users/${post.user_id}`}>{post.user}</a>
                                </div>
                            ))
                        )}
                    </div>

                    <div style={{
                        width: '28%',
                        overflowY: 'auto',
                        padding: '10px',
                        backgroundColor: '#fff',
                        borderRadius: '8px'
                    }}>
                        <button
                            style={{
                                alignSelf: 'flex-end',
                                padding: '8px',
                                backgroundColor: '#007BFF',
                                color: '#fff',
                                border: 'none',
                                borderRadius: '4px',
                                cursor: 'pointer',
                                margin: '10px',
                            }}
                            onClick={handleLogout}
                        >
                            Logout
                        </button>

                        <h1>Users</h1>
                        {users.length === 0 ? (
                            <p>Loading users...</p>
                        ) : (
                            <ul>
                                {users.map((user) => (
                                    <li key={user.user_id} style={{marginBottom: '5px'}}>
                                        <a href={`/users/${user.user_id}`}>{user.username}</a>
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div>
                </div>
            ) : (
                <LoginPage onLogin={handleLogin}/>
            )}
        </div>
    );
};


export default App;
