// UserProfile.js

import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';

const UserProfile = ({ token }) => {
  const { userId } = useParams();
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    console.log('Fetching user data for userId:', userId);
    console.log('Token:', token);

    const fetchUserData = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:5000/users/${userId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (response.ok) {
          const userData = await response.json();
          setUserData(userData);
        } else {
          console.error('Failed to fetch user data:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    if (userId && token) {
      fetchUserData();
    }
  }, [userId, token]);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '20px' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', width: '100%', marginBottom: '20px' }}>
        <div>
          <Link to="/posts" style={{ margin: '0 10px', textDecoration: 'none' }}>
            <button>Posts</button>
          </Link>
          <Link to={`/users/${userId}`} style={{ margin: '0 10px', textDecoration: 'none' }}>
            <button>My Profile</button>
          </Link>
        </div>
        <div>
          <Link to="/login" style={{ textDecoration: 'none' }}>
            <button style={{ padding: '8px', backgroundColor: '#007BFF', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
              Logout
            </button>
          </Link>
        </div>
      </header>
      <h1>User Profile</h1>
      {userData ? (
        <div style={{ border: '1px solid #ddd', borderRadius: '8px', padding: '20px', marginTop: '20px' }}>
          <p style={{ marginBottom: '10px', fontSize: '18px', fontWeight: 'bold' }}>User ID: {userData.user_id}</p>
          <p style={{ marginBottom: '10px', fontSize: '18px', fontWeight: 'bold' }}>Username: {userData.username}</p>
        </div>
      ) : (
        <p style={{ marginTop: '20px', fontSize: '18px' }}>Loading user profile...</p>
      )}
    </div>
  );
};

export default UserProfile;
