import React, { useState, useEffect } from 'react';

function App() {
  const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Fetch posts
    fetch("/posts")
      .then((res) => res.json())
      .then((data) => {
        setPosts(data);
        console.log(data);
      })
      .catch((error) => console.error('Error fetching posts:', error));

    // Fetch users
    fetch("/users")
      .then((res) => res.json())
      .then((data) => {
        setUsers(data);
        console.log(data);
      })
      .catch((error) => console.error('Error fetching users:', error));
  }, []);

  return (
    <div>
      <div>
        <h1>Posts</h1>
        {posts.length === 0 ? (
          <p>Loading posts...</p>
        ) : (
          posts.map((post) => (
            <div key={post.post_id}>
              <h3>{post.post_content}</h3>
              <p>User: {post.user}</p>
            </div>
          ))
        )}
      </div>

      <div>
        <h1>Users</h1>
        {users.length === 0 ? (
          <p>Loading users...</p>
        ) : (
          <ul>
            {users.map((user) => (
              <li key={user.user_id}>{user.username}</li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default App;
