// PostPage.js

import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import './App.css';
import { jwtDecode } from 'jwt-decode';
import Header from './Header';

const PostPage = ({ token }) => {
  const { post_id } = useParams();
  const [post, setPost] = useState(null);
  const [decodedToken, setDecodedToken] = useState({});
  const navigate = useNavigate();

  // Fetch post data
  useEffect(() => {
    const fetchPost = async () => {
      try {
        const response = await fetch(`/posts/${post_id}`);
        if (response.ok) {
          const postData = await response.json();
          setPost(postData);
        } else {
          console.error('Error fetching post:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching post:', error);
      }
    };

    fetchPost();
  }, [post_id]);

  // Handle logout
  const handleLogout = () => {
    sessionStorage.removeItem('token');
    navigate('/login');
  };

  // Decode token
  useEffect(() => {
    const storedToken = sessionStorage.getItem('token');
    if (storedToken) {
      const decoded = jwtDecode(storedToken);
      setDecodedToken(decoded);
    }
  }, []);

  // Handle post update
  const handleUpdatePost = async () => {
    try {
      // Prompt the user for the updated content
      const updatedContent = prompt('Enter the updated post content:', post.post_content);

      if (updatedContent !== null) {
        const response = await fetch(`/posts/${post_id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ post_content: updatedContent }),
        });

        if (response.ok) {
          // Reload the page to reflect the changes
          window.location.reload();
        } else {
          console.error('Error updating post:', response.statusText);
        }
      }
    } catch (error) {
      console.error('Error updating post:', error);
    }
  };

  // Handle post deletion
  const handleDeletePost = async () => {
    try {
      // Confirm with the user before deleting the post
      const shouldDelete = window.confirm('Are you sure you want to delete this post?');

      if (shouldDelete) {
        const response = await fetch(`/posts/${post_id}`, {
          method: 'DELETE',
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (response.ok) {
          // Navigate back to the posts page after deletion
          navigate('/posts');
        } else {
          console.error('Error deleting post:', response.statusText);
        }
      }
    } catch (error) {
      console.error('Error deleting post:', error);
    }
  };

  return (
    <div className="container">
      <Header token={token} decodedToken={decodedToken} handleLogout={handleLogout} />
      <div className="post-page">
        <h1>Post</h1>
        {post ? (
          <div className="post">
            <div className="post-header">
              Author: <Link to={`/users/${post.user_id}`} className="user-link">
                {post.user}
              </Link>
            </div>
            <div className="post-content">{post.post_content}</div>
            <div className="post-actions">
              {(decodedToken.user_id === post.user_id || decodedToken.is_admin) && (
                <>
                  <button onClick={handleUpdatePost} className="edit-post-button">
                    Edit Post
                  </button>
                  <button onClick={handleDeletePost} className="edit-post-button">
                    Delete Post
                  </button>
                </>
              )}
            </div>
          </div>
        ) : (
          <p>Loading post...</p>
        )}
      </div>
    </div>
  );
};

export default PostPage;
