//UserProfile.js

import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Header from './Header';
import './App.css';
import { jwtDecode } from 'jwt-decode';

const UserProfile = ({ token }) => {
  const [decodedToken, setDecodedToken] = useState({});
  const { userId } = useParams();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editedUsername, setEditedUsername] = useState('');
  const [isEditing, setEditing] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:5000/users/${userId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (response.ok) {
          const userData = await response.json();
          setUserData(userData);
          setEditedUsername(userData.username); // Set the initial edited username
        } else {
          console.error('Failed to fetch user data:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      } finally {
        setLoading(false);
      }
    };

    if (userId && token) {
      fetchUserData();
    }
  }, [userId, token]);

  const handleLogout = () => {
    sessionStorage.removeItem('token');
    navigate('/login');
  };

  useEffect(() => {
    const storedToken = sessionStorage.getItem('token');
    if (storedToken) {
      const decoded = jwtDecode(storedToken);
      setDecodedToken(decoded);
    }
  }, []);

  const handleEditProfile = () => {
    // Set isEditing to true to render the input for editing
    setEditing(true);
  };

  const handleSaveEdit = async () => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/users/${userId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ username: editedUsername }),
      });

      if (response.ok) {
        // Reset isEditing state after saving the edit
        setEditing(false);
        // Reload the page to reflect the changes
        window.location.reload();
      } else {
        console.error('Error updating user profile:', response.statusText);
      }
    } catch (error) {
      console.error('Error updating user profile:', error);
    }
  };

  const handleCancelEdit = () => {
    // Reset isEditing state without saving the edit
    setEditing(false);
    setEditedUsername('');
  };

  return (
    <div className="container">
      <Header token={token} decodedToken={decodedToken} handleLogout={handleLogout} />
      <div className="user-profile">
        <h1>User Profile</h1>
        {loading ? (
          <p className="loading-message">Loading user profile...</p>
        ) : userData ? (
          <div className="profile-info">
            {isEditing ? (
              <div className="edit-profile-form">
                <input
                  type="text"
                  value={editedUsername}
                  onChange={(e) => setEditedUsername(e.target.value)}
                  placeholder="Enter the edited username..."
                />
                <button onClick={handleSaveEdit} className="post-button">
                  Save
                </button>
                <button onClick={handleCancelEdit} className="cancel-button">
                  Cancel
                </button>
              </div>
            ) : (
              <>
                <p>User ID: {userData.user_id}</p>
                <p>Username: {userData.username}</p>
                {(decodedToken.user_id === userData.user_id || decodedToken.is_admin) && (
                  <button onClick={handleEditProfile} className="post-button">
                    Edit Profile
                  </button>
                )}
              </>
            )}
          </div>
        ) : (
          <p className="error-message">Failed to load user profile.</p>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
