// PostPage.js

import React, {useState, useEffect} from 'react';
import {useParams, Link, useNavigate} from 'react-router-dom';
import './App.css';
import {jwtDecode} from "jwt-decode";
import Header from "./Header";

const PostPage = ({token}) => {
    const {post_id} = useParams();
    const [post, setPost] = useState(null);
    const [decodedToken, setDecodedToken] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const response = await fetch(`/posts/${post_id}`);
                if (response.ok) {
                    const postData = await response.json();
                    setPost(postData);
                } else {
                    console.error('Error fetching post:', response.statusText);
                }
            } catch (error) {
                console.error('Error fetching post:', error);
            }
        };

        fetchPost();
    }, [post_id]);

    const handleLogout = () => {
        sessionStorage.removeItem('token');
        navigate('/login');
    };

    useEffect(() => {
        const storedToken = sessionStorage.getItem('token');
        if (storedToken) {
            const decoded = jwtDecode(storedToken);
            setDecodedToken(decoded);
        }
    }, []);

    return (
        <div className="container">
            <Header token={token} decodedToken={decodedToken} handleLogout={handleLogout}/>
            <div className="post-page">
                <h1>Post</h1>
                {post ? (
                    <div className="post">
                        <div className="post-header">
                            Author: <Link to={`/users/${post.user_id}`} className="user-link">
                            {post.user}
                        </Link>
                        </div>
                        <div className="post-content">
                            {post.post_content}
                        </div>
                        <div className="comments-section">
                            <h2 className="comments-heading">Comments</h2>
                            {post.comments && post.comments.length === 0 ? (
                                <p>No comments yet.</p>
                            ) : (
                                <ul className="comments-list">
                                    {post.comments && post.comments.map((comment) => (
                                        <li key={comment.comment_id} className="comment-item">
                                            <Link to={`/users/${comment.user_id}`} className="user-link">
                                                {comment.user}:
                                            </Link>
                                            <span>
                                            {comment.comment_content}
                                        </span>
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </div>
                    </div>
                ) : (
                    <p>Loading post...</p>
                )}
            </div>
        </div>
    );
};

export default PostPage;
