import React, { useState, useEffect } from 'react';

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("/posts")
      .then((res) => res.json())
      .then((data) => {
        setData(data);
        console.log(data);
      })
      .catch((error) => console.error('Error fetching posts:', error));
  }, []);

  return (
    <div>
      {data.length === 0 ? (
        <p>Loading...</p>
      ) : (
        data.map((post) => (
          <div key={post.post_id}>
            <h3>{post.post_content}</h3>
            <p>User: {post.user}</p>
          </div>
        ))
      )}
    </div>
  );
}

export default App;
