// PostPage.js

import React, {useState, useEffect} from 'react';
import {useParams, Link} from 'react-router-dom';
import './App.css';

const PostPage = () => {
    const [token, setToken] = useState('');
    const {post_id} = useParams();
    const [post, setPost] = useState(null);

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const response = await fetch(`/posts/${post_id}`);
                if (response.ok) {
                    const postData = await response.json();
                    setPost(postData);
                } else {
                    console.error('Error fetching post:', response.statusText);
                }
            } catch (error) {
                console.error('Error fetching post:', error);
            }
        };

        fetchPost();
    }, [post_id]);

    const handleLogout = () => {
        sessionStorage.removeItem('token');
        setToken('');
    };

    useEffect(() => {
        const storedToken = sessionStorage.getItem('token');
        if (storedToken) {
            setToken(storedToken);
            fetchData(storedToken);
        }
    }, [fetchData]);

    return (
        <div className="container">

            <header className="header">
                <div className="header-buttons">
                    <Link to="/posts" className="button">
                        <button>Posts</button>
                    </Link>
                    <Link to={token ? `/users/${decodedToken.user_id}` : '/login'} className="button">
                        <button>My Profile</button>
                    </Link>
                </div>
                <div>
                    <button className="logout-button" onClick={handleLogout}>
                        Logout
                    </button>
                </div>
            </header>
            <div className="post-page">
                <h1>Post</h1>
                {post ? (
                    <div className="post">
                        <div className="post-header">
                            Author: <Link to={`/users/${post.user_id}`} className="user-link">
                            {post.user}
                        </Link>
                        </div>
                        <div className="post-content">
                            {post.post_content}
                        </div>
                        <div className="comments-section">
                            <h2 className="comments-heading">Comments</h2>
                            {post.comments && post.comments.length === 0 ? (
                                <p>No comments yet.</p>
                            ) : (
                                <ul className="comments-list">
                                    {post.comments && post.comments.map((comment) => (
                                        <li key={comment.comment_id} className="comment-item">
                                            <Link to={`/users/${comment.user_id}`} className="user-link">
                                                {comment.user}:
                                            </Link>
                                            <span>
                                            {comment.comment_content}
                                        </span>
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </div>
                    </div>
                ) : (
                    <p>Loading post...</p>
                )}
            </div>
        </div>
    );
};

export default PostPage;
