import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import LoginPage from './LoginPage';
import UserProfile from './UserProfile';

function App() {
  const [token, setToken] = useState('');
  const [userId, setUserId] = useState(null);
  const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const postsResponse = await fetch('http://127.0.0.1:5000/posts', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const usersResponse = await fetch('http://127.0.0.1:5000/users', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (postsResponse.ok) {
          const postsData = await postsResponse.json();
          setPosts(postsData);
        }

        if (usersResponse.ok) {
          const usersData = await usersResponse.json();
          setUsers(usersData);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    if (token) {
      fetchData();
    }
  }, [token]);

  const handleLogin = async (newToken, newUserId) => {
    setToken(newToken);
    setUserId(newUserId);
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/login">Login</Link>
            </li>
            <li>
              <Link to="/user-profile">User Profile</Link>
            </li>
          </ul>
        </nav>

        <Routes>
          <Route
            path="/login"
            element={<LoginPage onLogin={handleLogin} />}
          />
          <Route
            path="/user-profile"
            element={<UserProfile userId={userId} token={token} />}
          />
          <Route
            path="/"
            element={<Home posts={posts} users={users} />}
          />
        </Routes>
      </div>
    </Router>
  );
}

const Home = ({ posts, users }) => (
  <div>
    <h1>Posts</h1>
    {posts.length === 0 ? (
      <p>Loading posts...</p>
    ) : (
      posts.map((post) => (
        <div key={post.post_id}>
          <h3>{post.post_content}</h3>
          <p>User: {post.user}</p>
        </div>
      ))
    )}

    <div>
      <h1>Users</h1>
      {users.length === 0 ? (
        <p>Loading users...</p>
      ) : (
        <ul>
          {users.map((user) => (
            <li key={user.user_id}>{user.username}</li>
          ))}
        </ul>
      )}
    </div>
  </div>
);

export default App;
