function App() {
    const [token, setToken] = useState('');
    const [posts, setPosts] = useState([]);
    const [users, setUsers] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const storedToken = sessionStorage.getItem('token');

        if (storedToken) {
            fetchData(storedToken);
        }
    }, []);

    const fetchData = async (token) => {
        try {
            const postsResponse = await fetch('/posts', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            const usersResponse = await fetch('/users', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (postsResponse.ok) {
                const postsData = await postsResponse.json();
                setPosts(postsData);
            } else if (postsResponse.status === 401 || postsResponse.status === 403) {
                sessionStorage.removeItem('token');
                navigate('/login'); // Use useNavigate to navigate to the login page
                return;
            }

            if (usersResponse.ok) {
                const usersData = await usersResponse.json();
                setUsers(usersData);
            } else if (usersResponse.status === 401 || usersResponse.status === 403) {
                sessionStorage.removeItem('token');
                navigate('/login'); // Use useNavigate to navigate to the login page
                return;
            }
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };
    return (
        <div>
            {token ? (
                <div>
                    <div>
                        <h1>Posts</h1>
                        {posts.length === 0 ? (
                            <p>Loading posts...</p>
                        ) : (
                            posts.map((post) => (
                                <div key={post.post_id}>
                                    <h3>{post.post_content}</h3>
                                    <p>User: {post.user}</p>
                                </div>
                            ))
                        )}
                    </div>

                    <div>
                        <h1>Users</h1>
                        {users.length === 0 ? (
                            <p>Loading users...</p>
                        ) : (
                            <ul>
                                {users.map((user) => (
                                    <li key={user.user_id}>{user.username}</li>
                                ))}
                            </ul>
                        )}
                    </div>
                </div>
            ) : (
                <LoginPage onLogin={setToken()}/>
            )}
        </div>
    );
}

export default App;
