// App.js

import React, {useState, useEffect, useCallback} from 'react';
import {useNavigate, useLocation} from 'react-router-dom';
import LoginPage from './LoginPage';
import RegisterPage from './RegisterPage';


const App = () => {
        const [token, setToken] = useState('');
        const [posts, setPosts] = useState([]);
        const [users, setUsers] = useState([]);
        const navigate = useNavigate();
        const location = useLocation();


        const fetchData = useCallback(async (token) => {
            try {
                const postsResponse = await fetch('/posts', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                const usersResponse = await fetch('/users', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                if (postsResponse.ok) {
                    const postsData = await postsResponse.json();
                    setPosts(postsData);
                } else {
                    sessionStorage.removeItem('token');
                    navigate('/login');
                    return;
                }

                if (usersResponse.ok) {
                    const usersData = await usersResponse.json();
                    setUsers(usersData);
                } else {
                    sessionStorage.removeItem('token');
                    navigate('/login');
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        }, [navigate]);

        const handleLogin = async (enteredToken) => {
            sessionStorage.setItem('token', enteredToken);
            setToken(enteredToken);
            await fetchData(enteredToken);
            navigate('/posts');
        };

        const handleLogout = () => {
            sessionStorage.removeItem('token');
            setToken('');
        };

        useEffect(() => {
            const storedToken = sessionStorage.getItem('token');

            if (storedToken) {
                setToken(storedToken);
                fetchData(storedToken);
            }
        }, [fetchData]);

        useEffect(() => {
            fetchData(token); // Fetch data whenever the location changes
        }, [location.pathname, token, fetchData]);

        return (
            <div style={{display: 'flex', flexDirection: 'column', height: '100vh'}}>
                {token ? (
                    <div style={{flex: 1}}>
                        <div style={{display: 'flex', justifyContent: 'space-between', padding: '10px'}}>

                            <div>
                                <h1>Posts</h1>
                                {posts.length === 0 ? (
                                    <p>Loading posts...</p>
                                ) : (
                                    posts.map((post) => (
                                        <div key={post.post_id}>
                                            <h3>{post.post_content}</h3>
                                            <p>User: {post.user}</p>
                                        </div>
                                    ))
                                )}
                            </div>
                            <div>
                                <h1>Users</h1>
                                {users.length === 0 ? (
                                    <p>Loading users...</p>
                                ) : (
                                    <ul>
                                        {users.map((user) => (
                                            <li key={user.user_id}>
                                                <a href={`/users/${user.user_id}`}>{user.username}</a>
                                            </li>
                                        ))}
                                    </ul>
                                )}
                            </div>
                            <button style={{alignSelf: 'flex-start'}} onClick={handleLogout}>
                                Logout
                            </button>
                        </div>
                    </div>
                ) : (
                    <LoginPage onLogin={handleLogin}/>
                )}
            </div>
        );
    }
;

export default App;
